import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor() {
    super();

    this.state = {
      monsters: [
        {
          name: "Frankenstein"
        },
        {
          name: "Dracula"
        },        
        {
          name: "Zombie"
        },
        {
          name: "Your mom"
        },
      ]
    }
  }

  render(){
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            {this.state.name}
          </p>
          <button onClick= {() => this.setState({name: "Чё жмёшь кнопку, пёс?"})} >Change text</button>
        </header>
      </div>
    );
  }
}

export default App;
